{
  "service": "VisualCrossingWeather",
  "version": "3.1.0",
  "description": "Fetches weather data for a specified location using Visual Crossing Web Services.",
  "baseUrl": "https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline",
  "method": "GET",
  "path": "/{location}",
  "queryParameters": {
    "key": "3GTBGDK4SFR2THWVFTY5JRRFP"
  },
  "parameters": {
    "location": {
      "description": "The location for which to fetch weather data, in the format City,CountryCode.",
      "type": "string",
      "required": true,
      "example": "London,UK"
    }
  },
  "responses": {
    "200": {
      "description": "Successful response with weather data."
    },
    "400": {
      "description": "Bad request. The request was unacceptable, often due to missing a required parameter."
    },
    "401": {
      "description": "Unauthorized. The API key is invalid."
    },
    "404": {
      "description": "Not Found. The requested resource doesn't exist."
    }
  }
}
